import React, { useState } from 'react';
import VendorSidebar from '../components/VendorSidebar'; // ✅ Import sidebar

const AccountSettings = () => {
  const [formData, setFormData] = useState({
    name: 'Local Vendor',
    email: 'vendor@example.com',
    phone: '555-123-4567',
    businessName: 'Best Handmade Goods',
    address: '123 Main St, Elgin, IL 60120',
    password: '',
    confirmPassword: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.password && formData.password !== formData.confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }
    setMessage("Account settings updated successfully.");
  };

  return (
    <div className="flex min-h-screen">
      <VendorSidebar />
      <div className="flex-1 p-8 bg-[#f4f8fc] font-sans">
        <h1 className="text-3xl font-bold text-[#003366] mb-6">Account Settings</h1>
        {message && <p className="text-green-700 font-semibold mb-4">{message}</p>}

        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg max-w-xl">
          <label className="font-bold text-[#003366] block mb-1">Full Name</label>
          <input name="name" value={formData.name} onChange={handleChange} style={inputStyle} />

          <label className="font-bold text-[#003366] block mb-1">Email</label>
          <input name="email" type="email" value={formData.email} onChange={handleChange} style={inputStyle} />

          <label className="font-bold text-[#003366] block mb-1">Phone</label>
          <input name="phone" value={formData.phone} onChange={handleChange} style={inputStyle} />

          <label className="font-bold text-[#003366] block mb-1">Business Name</label>
          <input name="businessName" value={formData.businessName} onChange={handleChange} style={inputStyle} />

          <label className="font-bold text-[#003366] block mb-1">Business Address</label>
          <input name="address" value={formData.address} onChange={handleChange} style={inputStyle} />

          <hr className="my-6" />

          <label className="font-bold text-[#003366] block mb-1">New Password</label>
          <input name="password" type="password" value={formData.password} onChange={handleChange} style={inputStyle} />

          <label className="font-bold text-[#003366] block mb-1">Confirm Password</label>
          <input name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} style={inputStyle} />

          <button type="submit" style={saveButtonStyle}>Save Changes</button>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  fontSize: '1rem',
  borderRadius: '6px',
  border: '1px solid #ccc',
  marginBottom: '1rem'
};

const saveButtonStyle = {
  backgroundColor: '#003366',
  color: 'white',
  padding: '0.75rem 1.5rem',
  border: 'none',
  borderRadius: '8px',
  fontSize: '1rem',
  cursor: 'pointer',
  marginTop: '1rem'
};

export default AccountSettings;
