import React from 'react';
import VendorSidebar from '../components/VendorSidebar';
import {
  LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer,
  BarChart, Bar, PieChart, Pie, Cell, Legend
} from 'recharts';

const AnalyticsReports = () => {
  const salesData = [
    { date: 'May 1', sales: 5 },
    { date: 'May 2', sales: 9 },
    { date: 'May 3', sales: 6 },
    { date: 'May 4', sales: 10 },
    { date: 'May 5', sales: 7 },
    { date: 'May 6', sales: 12 },
    { date: 'May 7', sales: 8 },
  ];

  const productPerformance = [
    { name: 'Necklace', units: 18 },
    { name: 'Soap', units: 14 },
    { name: 'T-Shirt', units: 9 },
  ];

  const revenueByCategory = [
    { name: 'Jewelry', value: 320 },
    { name: 'Health & Beauty', value: 210 },
    { name: 'Clothing', value: 180 }
  ];

  const COLORS = ['#003366', '#00509e', '#0096c7'];

  return (
    <div className="flex min-h-screen bg-[#f4f8fc] font-sans">
      <VendorSidebar />
      <div className="flex-1 p-8">
        <h1 className="text-3xl font-bold text-[#003366] mb-8">Analytics & Reports</h1>

        <section className="mb-12">
          <h2 className="text-xl text-[#00509e] mb-2">Sales Over Time</h2>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={salesData}>
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="sales" stroke="#003366" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </section>

        <section className="mb-12">
          <h2 className="text-xl text-[#00509e] mb-2">Top Product Performance</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={productPerformance}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="units" fill="#00509e" />
            </BarChart>
          </ResponsiveContainer>
        </section>

        <section>
          <h2 className="text-xl text-[#00509e] mb-2">Revenue by Category</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie data={revenueByCategory} dataKey="value" nameKey="name" outerRadius={100} fill="#8884d8" label>
                {revenueByCategory.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </section>
      </div>
    </div>
  );
};

export default AnalyticsReports;
