import React from 'react';
import VendorSidebar from '../components/VendorSidebar';

const VendorSupport = () => {
  return (
    <div className="flex min-h-screen">
      <VendorSidebar />
      <div className="flex-1 p-8">
        <h1 className="text-3xl font-bold mb-6">Vendor Support</h1>
        <p className="text-gray-700 mb-4">Need help? Reach out to our support team below or check out our helpful resources.</p>

        <div className="bg-white shadow-md rounded p-6 mb-6">
          <h2 className="text-xl font-semibold mb-2">Contact Support</h2>
          <p>Email us at <a href="mailto:support@localvendorsbazaar.com" className="text-blue-600 underline">support@localvendorsbazaar.com</a></p>
        </div>

        <div className="bg-white shadow-md rounded p-6">
          <h2 className="text-xl font-semibold mb-2">Helpful Links</h2>
          <ul className="list-disc list-inside text-gray-600">
            <li><a href="/faq" className="text-blue-600 underline">Frequently Asked Questions</a></li>
            <li><a href="/vendor-signup" className="text-blue-600 underline">Become a Vendor</a></li>
            <li><a href="/contact" className="text-blue-600 underline">Contact Us</a></li>
            <li><a href="/blog" className="text-blue-600 underline">Tips for Selling Locally</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default VendorSupport;
