import React, { useState } from 'react';
import VendorSidebar from '../components/VendorSidebar';

const AccountSettings = () => {
  const [formData, setFormData] = useState({
    name: 'Local Vendor',
    email: 'vendor@example.com',
    phone: '555-123-4567',
    businessName: 'Best Handmade Goods',
    address: '123 Main St, Elgin, IL 60120',
    password: '',
    confirmPassword: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.password && formData.password !== formData.confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }
    setMessage("Account settings updated successfully.");
    // Logic to save data would go here
  };

  return (
    <div className="flex min-h-screen bg-[#f4f8fc]">
      <VendorSidebar />
      <div className="flex-1 p-8">
        <h1 className="text-3xl font-bold mb-6 text-[#003366]">Account Settings</h1>

        {message && <p className="text-green-600 font-semibold mb-4">{message}</p>}

        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md max-w-xl">
          <label className="block font-semibold mb-1 text-[#003366]">Full Name</label>
          <input name="name" value={formData.name} onChange={handleChange} className={inputStyle} />

          <label className="block font-semibold mb-1 text-[#003366]">Email</label>
          <input name="email" type="email" value={formData.email} onChange={handleChange} className={inputStyle} />

          <label className="block font-semibold mb-1 text-[#003366]">Phone</label>
          <input name="phone" value={formData.phone} onChange={handleChange} className={inputStyle} />

          <label className="block font-semibold mb-1 text-[#003366]">Business Name</label>
          <input name="businessName" value={formData.businessName} onChange={handleChange} className={inputStyle} />

          <label className="block font-semibold mb-1 text-[#003366]">Business Address</label>
          <input name="address" value={formData.address} onChange={handleChange} className={inputStyle} />

          <hr className="my-6" />

          <label className="block font-semibold mb-1 text-[#003366]">New Password</label>
          <input name="password" type="password" value={formData.password} onChange={handleChange} className={inputStyle} />

          <label className="block font-semibold mb-1 text-[#003366]">Confirm Password</label>
          <input name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} className={inputStyle} />

          <button type="submit" className="bg-[#003366] text-white px-6 py-2 rounded-md mt-4 font-semibold">Save Changes</button>
        </form>
      </div>
    </div>
  );
};

const inputStyle = "w-full px-4 py-2 mb-4 border rounded-md border-gray-300";

export default AccountSettings;
